schema.customcss:
.card-dynamic-wrapper {
  height: 100%;
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
}
.card-dynamic {
  height: 180pt;
  width: 350pt;
  background-color: #222;
  display: inline-block;
  border-radius: 10pt;
  transition: 0.2s ease-in-out;
  box-shadow: 0px 0px 50px rgba(0, 0, 0, 0.25);
}

.card-dynamic:link, .card-dynamic-header:active, .card-dynamic:visited {
  color: #fff;
  text-decoration: none;
}
.card-dynamic:hover {
  transform: scale(1.03);
}
.card-dynamic:active {
  transform: scale(0.99);
  opacity: 0.7;
}
.card-dynamic-icon {
  width: 70pt;
  margin-left: 25pt;
  margin-top: 35pt;
  opacity: 0.5;
}
.card-dynamic-header {
  font-weight: bold;
  font-size: 20pt;
  margin: 0;
  margin-left: 25pt;
  margin-top: 15pt;
}

/*
  Color variations
*/

.card-dynamic.pink {
    background-image: linear-gradient( 135deg, #FE86C3, #B33675);
}
.card-dynamic.green {
    background-image: linear-gradient( 135deg, #79D4B8, #459989);
}
.card-dynamic.orange {
    background-image: linear-gradient( 135deg, #E47D57, #C93F1F);
}
.card-dynamic.red {
    background-image: linear-gradient( 135deg, #E7414D, #B3252D);
}
.card-dynamic.yellow {
    background-image: linear-gradient( 135deg, #E9BE40, #CD812E);
}
.card-dynamic.purple {
    background-image: linear-gradient( 135deg, #8361F2, #4623B1);
}
.card-dynamic.blue {
    background-image: linear-gradient( 135deg, #78BAFF, #2E6BC3);
}
.card-dynamic.gray {
    background-image: linear-gradient( 135deg, #8D9196, #525458);
}
